import{_ as e,o as t,c as a,a as s}from"./app.1eab8682.js";const u=JSON.parse('{"title":"biome 使用","description":"","frontmatter":{},"headers":[{"level":2,"title":"biome 初体验","slug":"biome-初体验","link":"#biome-初体验","children":[]},{"level":2,"title":"小结","slug":"小结","link":"#小结","children":[]},{"level":2,"title":"参考","slug":"参考","link":"#参考","children":[]}],"relativePath":"node/how-to-use-biomejs.md"}'),r={name:"node/how-to-use-biomejs.md"},o=s(`<h1 id="biome-使用" tabindex="-1">biome 使用 <a class="header-anchor" href="#biome-使用" aria-hidden="true">#</a></h1><p><a href="https://biomejs.dev/" target="_blank" rel="noreferrer">biome</a> 是 rome 的继承者，集代<strong>码格式化</strong>、<strong>代码检查</strong>、<strong>代码测试</strong>、代码构建等功能于一身，是一个功能很多的新兴工具，速度极快。</p><blockquote><p>rome 在 2023 年 9 月份宣布停止维护，biome 是它的继承者，目前开发活跃。</p></blockquote><p>功能特色：</p><ul><li>格式化和 lint 统一：biome 会自动格式化代码，并且会在格式化的同时进行代码检查，保证代码风格的一致性。</li><li>性能极快：底层使用 Rust 编写，速度极快。</li><li>易于使用：配置很少，开箱即用。</li><li>提示友好：错误提示友好，有详细的错误信息。</li><li>插件生态丰富：支持插件，可以扩展功能。</li></ul><p>和 eslint + prettier 的比较：</p><table><thead><tr><th>功能</th><th>biome</th><th>eslint + prettier</th></tr></thead><tbody><tr><td>配置</td><td>融合</td><td>分离，eslint 和 prettier 的配置是分离的</td></tr><tr><td>性能</td><td>极快</td><td>慢</td></tr><tr><td>可扩展</td><td>插件</td><td>插件</td></tr><tr><td>社区情况</td><td>快速增长，还不太成熟</td><td>繁荣，比较成熟了</td></tr></tbody></table><h2 id="biome-初体验" tabindex="-1">biome 初体验 <a class="header-anchor" href="#biome-初体验" aria-hidden="true">#</a></h2><p>需要 node14.18 以上版本，可以通过 nvm 升级 node 版本。</p><p>安装：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">pnpm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">i</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-D</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-E</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">@biome/biome</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-w</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 安装到 workspace</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="小结" tabindex="-1">小结 <a class="header-anchor" href="#小结" aria-hidden="true">#</a></h2><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-hidden="true">#</a></h2><ul><li><a href="https://blog.tericcabrel.com/nodejs-typescript-biome/" target="_blank" rel="noreferrer">Set up a Node.js project with TypeScript and Biome</a></li><li><a href="https://walterspieler.dev/blog/biome-js-prettier-eslint-killer" target="_blank" rel="noreferrer">Biome.js : Prettier+ESLint killer ?</a></li><li><a href="https://github.com/biomejs/biome/discussions/1281" target="_blank" rel="noreferrer">Comparison with OXC #1281</a></li></ul>`,14),i=[o];function l(n,d,p,c,h,b){return t(),a("div",null,i)}const _=e(r,[["render",l]]);export{u as __pageData,_ as default};
