import{_ as e,o as t,c as a,a as n}from"./app.4b7f6d92.js";const m=JSON.parse('{"title":"一些杂问题","description":"","frontmatter":{},"headers":[{"level":2,"title":"npm init vue@latest 与 npm create vite@latest 的区别?","slug":"npm-init-vue-latest-与-npm-create-vite-latest-的区别","link":"#npm-init-vue-latest-与-npm-create-vite-latest-的区别","children":[]}],"relativePath":"vue3/一些杂问题.md"}'),i={name:"vue3/一些杂问题.md"},c=n('<h1 id="一些杂问题" tabindex="-1">一些杂问题 <a class="header-anchor" href="#一些杂问题" aria-hidden="true">#</a></h1><h2 id="npm-init-vue-latest-与-npm-create-vite-latest-的区别" tabindex="-1">npm init vue@latest 与 npm create vite@latest 的区别? <a class="header-anchor" href="#npm-init-vue-latest-与-npm-create-vite-latest-的区别" aria-hidden="true">#</a></h2><p><code>npm init vue@latest</code> 创建一个 vue3 项目，会让你选择项目配置，比如是否使用 typescript，是否使用 eslint 等。</p><blockquote><p>@latest 不能省略，否则 npm 使用老的版本。</p></blockquote><p><code>npm create vite@latest</code> 创建一个 vite 项目，会让你选择你想要的框架，比如 vue，react，svelte 等。</p><blockquote><p>@latest 似乎可以省略，没查到文档，但是不加也没问题。</p></blockquote><blockquote><p>相同点，都使用 vite 来构建项目。</p></blockquote><blockquote><p>如何创建一个vue2的的项目？</p></blockquote><p><code>npm init vue@legacy</code> 。</p><p>参考文章</p><p><a href="https://lxchuan12.gitee.io/create-vue/" target="_blank" rel="noreferrer">create-vue，未来将替代 Vue-CLI</a></p>',11),o=[c];function p(r,l,s,u,_,d){return t(),a("div",null,o)}const h=e(i,[["render",p]]);export{m as __pageData,h as default};
